import React from 'react'
import './style.css'

const test = ()=>{
    class Reservation extends React.Component {
        constructor(props) {
          super(props);
          this.state = {
            isGoing: true,
            numberOfGuests: 2
          };

          this.handleInputChange = this.handleInputChange.bind(this);
        }

        handleInputChange(event) {
          const target = event.target;
          const value = target.type === 'checkbox' ? target.checked : target.value;
          const name = target.name;

          this.setState({
            [name]: value
          });
        }

      }

   return (
    <div>
        <h1>This is valid Input</h1>
        <input type="text" className="formControl" placeholder="Enter Your Name Here!" />
        <h1>This is Disabled Input</h1>
        <input type="text" className="formControl" placeholder="" disabled="disabled" />
        <h1>Input Box with errors</h1>
        {/* <input type="text" className="formControl" placeholder="" /> */}
        <input type="number" pattern="[0-9]*" inputmode="numeric"></input>
        <form>
          <label>
            Is going:
            <input
              name="isGoing"
              type="checkbox"
              checked={this.state.isGoing}
              onChange={this.handleInputChange} />
          </label>
          <label>
            Number of guests:
            <input
              name="numberOfGuests"
              type="number"
              value={this.state.numberOfGuests}
              onChange={this.handleInputChange} />
          </label>
        </form>
    </div>

   )
}
export default test;
